mapper.select:
select id from sys_dept where 1=1

	and enable_ = #{enable}


	and id = #{id}


	and parent_id = #{parentId}


	and dept_no = #{deptNo}


	and id in (select id from sys_dept start with id=#{rootNode} connect by prior id = parent_id)


	and (dept_name like '%'||#{deptName}||'%')

order by dept_no
